mapper.select:
  	select 
  	  sum(point_change) 
	from energy_trade_record 
    	where user_uuid=#{userUuid}
    	and in_or_out=1 
      	and time_created between #{beginTime} and #{endTime}

  	select 
  	  sum(point_change) 
	from energy_trade_record 
    	where user_uuid=#{userUuid}
    	and in_or_out=0 
      	and time_created between #{beginTime} and #{endTime}

	select 

  	from energy_trade_record 
    where user_uuid=#{userUuid}
    and in_or_out=1 
    and status = 1
    and time_created between #{beginTime} and #{endTime}

mapper.update:
	update energy_trade_record set
	  status = #{newStatus}
  where uuid = #{uuid}
