mapper.select:
  	select 
  	  sum(point_change) 
	from point_trade_record 
    	where user_uuid=#{userUuid}
    	and in_or_out=1 
      	and created between #{beginTime} and #{endTime}

  	select 
  	  sum(point_change) 
	from point_trade_record 
    	where user_uuid=#{userUuid}
    	and in_or_out=0 
      	and created between #{beginTime} and #{endTime}

	select 

  	from point_trade_record
    where user_uuid=#{userUuid}
    and in_or_out=1 
    and status = 1
    and created between #{beginTime} and #{endTime}

mapper.update:
	update point_trade_record set
	  status = #{newStatus}
  where uuid = #{uuid}
